      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'L·ªói kh√¥ng x√°c ƒë·ªãnh');
      }

      setRisk(data.risk);
      setForecast(Array.isArray(data.forecast) ? data.forecast : []);
      setError(null);
    } catch (err) {
      console.error('L·ªói:', err.message);
      setError(err.message);
      setRisk(null);
      setForecast([]);
    }
  };

  return (
    <div className="app-container">
      <h1>üöõ ·ª®ng d·ª•ng ƒë√°nh gi√° nguy c∆° v·∫≠n t·∫£i</h1>
