import React, { useState } from 'react';
import './App.css';

function App() {
  const [province, setProvince] = useState('');
  const [month, setMonth] = useState('');
  const [risk, setRisk] = useState('');
  const [weather, setWeather] = useState([]);
  const [trafficPoints, setTrafficPoints] = useState([]);

  const provinces = [
    "Hà Nội", "TP. HCM", "Đà Nẵng", "Hải Phòng", "Cần Thơ", "Bình Dương",
    "Đồng Nai", "Thanh Hóa", "Nghệ An", "Thừa Thiên Huế", "Quảng Ninh",
    "Hải Dương", "Nam Định", "Thái Bình", "Vĩnh Phúc", "Bắc Ninh",
    "Bắc Giang", "Hưng Yên", "Hòa Bình", "Lào Cai", "Yên Bái", "Tuyên Quang",
    "Hà Giang", "Lạng Sơn", "Cao Bằng", "Điện Biên", "Sơn La", "Lai Châu",
    "Kon Tum", "Gia Lai", "Đắk Lắk", "Lâm Đồng", "Kiên Giang", "Cà Mau"
  ];

  const months = ["01", "02", "03", "04", "05", "06", "07", "08", "09", "10", "11", "12"];

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch('https://localhost:5000/api/check-risk', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ province, month })
      });

      const data = await res.json();
      setRisk(data.risk);
      setWeather(data.weather);
      setTrafficPoints(data.trafficPoints || []);
    } catch (err) {
      alert("Không thể kết nối tới máy chủ.");
      console.error(err);
    }
  };

  return (
    <div className="app-container">
      <div className="form-container">
        <h2>Đánh giá nguy cơ vận tải</h2>
        <form onSubmit={handleSubmit}>
          <select value={province} onChange={e => setProvince(e.target.value)} required>
            <option value="">Chọn tỉnh</option>
            {provinces.map((p, index) => (
              <option key={index} value={p}>{p}</option>
            ))}
          </select>

          <select value={month} onChange={e => setMonth(e.target.value)} required>
            <option value="">Chọn tháng</option>
            {months.map((m, index) => (
              <option key={index} value={m}>Tháng {m}</option>
            ))}
          </select>

          <button type="submit">Kiểm tra</button>
        </form>

        {risk && (
          <div className="result">
            <p>📊 Mức nguy cơ: <strong>{risk}</strong></p>

            {Array.isArray(weather) && weather.length > 0 && (
              <>
                <p>📅 Dự báo thời tiết 5 ngày:</p>
                <ul>
                  {weather.map((w, idx) => <li key={idx}>{w}</li>)}
                </ul>
              </>
            )}

            {trafficPoints.length > 0 && (
              <>
                <p>🚧 Điểm nghẽn tại {province}:</p>
                <ul>
                  {trafficPoints.map((p, idx) => (
                    <li key={idx}>{p}</li>
                  ))}
                </ul>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
