import React, { useState } from 'react';
import './App.css';

function App() {
  const [province, setProvince] = useState('');
  const [risk, setRisk] = useState('');
  const [forecast, setForecast] = useState([]);
  const [trafficPoints, setTrafficPoints] = useState([]);

  const provinces = [
    "An Giang", "Bà Rịa - Vũng Tàu", "Bạc Liêu", "Bắc Giang", "Bắc Kạn", "Bắc Ninh", "Bến Tre",
    "Bình Dương", "Bình Định", "Bình Phước", "Bình Thuận", "Cà Mau", "Cao Bằng", "Cần Thơ",
    "Đà Nẵng", "Đắk Lắk", "Đắk Nông", "Điện Biên", "Đồng Nai", "Đồng Tháp", "Gia Lai",
    "Hà Giang", "Hà Nam", "Hà Nội", "Hà Tĩnh", "Hải Dương", "Hải Phòng", "Hậu Giang",
    "Hòa Bình", "Hưng Yên", "Khánh Hòa", "Kiên Giang", "Kon Tum", "Lai Châu", "Lâm Đồng",
    "Lạng Sơn", "Lào Cai", "Long An", "Nam Định", "Nghệ An", "Ninh Bình", "Ninh Thuận",
    "Phú Thọ", "Phú Yên", "Quảng Bình", "Quảng Nam", "Quảng Ngãi", "Quảng Ninh", "Quảng Trị",
    "Sóc Trăng", "Sơn La", "Tây Ninh", "Thái Bình", "Thái Nguyên", "Thanh Hóa", "Thừa Thiên Huế",
    "Tiền Giang", "TP. HCM", "Trà Vinh", "Tuyên Quang", "Vĩnh Long", "Vĩnh Phúc", "Yên Bái"
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();
    setRisk('');
    setForecast([]);
    setTrafficPoints([]);

    try {
      const res = await fetch('https://localhost:5000/api/check-risk', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ province })
      });

      const data = await res.json();
      setRisk(data.risk);
      setForecast(data.forecast || []);
      setTrafficPoints(data.trafficPoints || []);
    } catch (err) {
      console.error('Lỗi kết nối backend:', err);
      alert('Không thể kết nối máy chủ. Hãy chắc chắn backend đang chạy.');
    }
  };

  return (
    <div className="app-container">
      <div className="form-container">
        <h2>Đánh giá nguy cơ vận tải</h2>
        <form onSubmit={handleSubmit}>
          <select value={province} onChange={e => setProvince(e.target.value)} required>
            <option value="">Chọn tỉnh</option>
            {provinces.map((p, index) => (
              <option key={index} value={p}>{p}</option>
            ))}
          </select>

          <button type="submit">Kiểm tra</button>
        </form>

        {risk && (
          <div className="result">
            <p>📊 Mức nguy cơ: <strong>{risk}</strong></p>

            {forecast.length > 0 && (
              <>
                <p>⛅ Dự báo thời tiết 5 ngày tới:</p>
                <ul>
                  {forecast.map((item, idx) => (
                    <li key={idx}>
                      {item.date}: {item.weather} ({item.temp}°C)
                    </li>
                  ))}
                </ul>
              </>
            )}

            {trafficPoints.length > 0 && (
              <>
                <p>🚧 Điểm nghẽn tại {province}:</p>
                <ul>
                  {trafficPoints.map((p, idx) => (
                    <li key={idx}>{p}</li>
                  ))}
                </ul>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
