import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

const provinces = [
  'Hà Nội', 'Huế', 'Lai Châu', 'Điện Biên', 'Sơn La', 'Lạng Sơn',
  'Quảng Ninh', 'Thanh Hóa', 'Nghệ An', 'Hà Tĩnh', 'Cao Bằng',
  'Tuyên Quang', 'Lào Cai', 'Thái Nguyên', 'Phú Thọ',
  'Bắc Ninh', 'Hưng Yên', 'Hải Phòng', 'Ninh Bình',
  'Quảng Trị', 'Đà Nẵng', 'Quảng Ngãi', 'Gia Lai',
  'Khánh Hòa', 'Lâm Đồng', 'Đắk Lắk', 'TP. HCM',
  'Đồng Nai', 'Tây Ninh', 'TP. Cần Thơ', 'Vĩnh Long',
  'Đồng Tháp', 'Cà Mau', 'An Giang'
];

const months = Array.from({ length: 12 }, (_, i) => i + 1);

function App() {
  const [province, setProvince] = useState('');
  const [month, setMonth] = useState(1);
  const [risk, setRisk] = useState(null);
  const [weather, setWeather] = useState(null);

  const handleSubmit = async () => {
    try {
      const response = await axios.post('https://your-backend.onrender.com/api/check-risk', {
        province,
        month,
      });
      setRisk(response.data.risk);
      setWeather(response.data.weather);
    } catch (error) {
      console.error('Không thể kết nối đến máy chủ:', error);
    }
  };

  return (
    <div className="app-container">
      <div className="form-container">
        <h1>Đánh Giá Nguy Cơ Vận Tải</h1>
        <select onChange={(e) => setProvince(e.target.value)} value={province}>
          <option value="">-- Chọn tỉnh/thành --</option>
          {provinces.map((prov) => (
            <option key={prov} value={prov}>{prov}</option>
          ))}
        </select>

        <select onChange={(e) => setMonth(Number(e.target.value))} value={month}>
          {months.map((m) => (
            <option key={m} value={m}>Tháng {m}</option>
          ))}
        </select>

        <button onClick={handleSubmit}>Đánh giá</button>

        {risk && (
          <div className="result">
            <h3>Kết quả: {risk}</h3>
            {weather && <p>Thời tiết: {weather}</p>}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
