import React, { useState } from 'react';
import './App.css';

function App() {
  const [province, setProvince] = useState('');
  const [risk, setRisk] = useState('');
  const [weatherList, setWeatherList] = useState([]);
  const [trafficPoints, setTrafficPoints] = useState([]);

  const provinces = [
    "An Giang", "Bà Rịa - Vũng Tàu", "Bắc Giang", "Bắc Kạn", "Bạc Liêu", "Bắc Ninh", "Bến Tre", "Bình Định",
    "Bình Dương", "Bình Phước", "Bình Thuận", "Cà Mau", "Cần Thơ", "Cao Bằng", "Đà Nẵng", "Đắk Lắk",
    "Đắk Nông", "Điện Biên", "Đồng Nai", "Đồng Tháp", "Gia Lai", "Hà Giang", "Hà Nam", "Hà Nội",
    "Hà Tĩnh", "Hải Dương", "Hải Phòng", "Hậu Giang", "Hòa Bình", "Hưng Yên", "Khánh Hòa", "Kiên Giang",
    "Kon Tum", "Lai Châu", "Lâm Đồng", "Lạng Sơn", "Lào Cai", "Long An", "Nam Định", "Nghệ An",
    "Ninh Bình", "Ninh Thuận", "Phú Thọ", "Phú Yên", "Quảng Bình", "Quảng Nam", "Quảng Ngãi", "Quảng Ninh",
    "Quảng Trị", "Sóc Trăng", "Sơn La", "Tây Ninh", "Thái Bình", "Thái Nguyên", "Thanh Hóa", "Thừa Thiên Huế",
    "Tiền Giang", "TP. HCM", "Trà Vinh", "Tuyên Quang", "Vĩnh Long", "Vĩnh Phúc", "Yên Bái"
  ];

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch('https://localhost:5000/api/check-risk', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ province })
      });

      const data = await res.json();

      if (res.ok) {
        setRisk(data.risk);
        setWeatherList(data.weatherList || []);
        setTrafficPoints(data.trafficPoints || []);
      } else {
        alert('Không thể lấy dữ liệu. Vui lòng thử lại.');
      }
    } catch (err) {
      console.error(err);
      alert('Lỗi kết nối tới máy chủ!');
    }
  };

  return (
    <div className="app-container">
      <div className="form-container">
        <h2>Đánh giá nguy cơ vận tải</h2>
        <form onSubmit={handleSubmit}>
          <select value={province} onChange={e => setProvince(e.target.value)} required>
            <option value="">Chọn tỉnh</option>
            {provinces.map((p, index) => (
              <option key={index} value={p}>{p}</option>
            ))}
          </select>

          <button type="submit">Kiểm tra</button>
        </form>

        {risk && (
          <div className="result">
            <p>📊 Mức nguy cơ: <strong>{risk}</strong></p>

            {weatherList.length > 0 && (
              <>
                <p>🌤️ Dự báo thời tiết 5 ngày:</p>
                <ul>
                  {weatherList.map((w, idx) => (
                    <li key={idx}>{w}</li>
                  ))}
                </ul>
              </>
            )}

            {trafficPoints.length > 0 && (
              <>
                <p>🚧 Điểm nghẽn tại {province}:</p>
                <ul>
                  {trafficPoints.map((p, idx) => (
                    <li key={idx}>{p}</li>
                  ))}
                </ul>
              </>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
